import React, { useState } from 'react';
import axios from 'axios';

function App() {
  // State for DNA input and the processed result
  const [dnaSequence, setDnaSequence] = useState(''); // Input DNA sequence
  const [processedDna, setProcessedDna] = useState(''); // Processed DNA sequence
  const [loading, setLoading] = useState(false); // Loading state

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent form submission from reloading the page

    if (!dnaSequence) {
      alert('Please enter a DNA sequence');
      return; // Stop the process if the input is empty
    }

    setLoading(true); // Set loading state while waiting for the response

    try {
      // Sending POST request to the Node.js server to process the DNA sequence
      const response = await axios.post('http://localhost:3001/process-dna', {
        dnaSequence: dnaSequence, // Send the DNA sequence to the server
      });

      // Set the processed DNA sequence received from the server
      setProcessedDna(response.data.processedDna); // Set the processed DNA from the response
    } catch (error) {
      console.error('Error processing DNA:', error);
      alert('Error processing DNA sequence');
    } finally {
      setLoading(false); // Reset loading state after receiving the response
    }
  };

  return (
    <div className="App">
      <h1>DNA Sequence Processor</h1>

      {/* Form to input DNA sequence */}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Enter DNA Sequence: </label>
          <input
            type="text"
            value={dnaSequence} // Input value bound to state
            onChange={(e) => setDnaSequence(e.target.value)} // Update state on change
            placeholder="Enter DNA sequence"
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Processing...' : 'Process DNA'}
        </button>
      </form>

      {/* Display the processed DNA sequence */}
      {processedDna && (
        <div>
          <h2>Processed DNA Sequence</h2>
          <p>{processedDna}</p> {/* Show the processed DNA result */}
        </div>
      )}
    </div>
  );
}

export default App;
